const response = items[0].json;
const nodes = response.nodes || [];

const categories = [
  { category: 'shopping', query_key: 'shop' },
  { category: 'dining', query_key: 'amenity', query_val: 'restaurant|cafe|fast_food' },
  { category: 'convenience', query_key: 'shop', query_val: 'convenience' },
  { category: 'medical', query_key: 'amenity', query_val: 'pharmacy|clinic|hospital' },
  { category: 'leisure', query_key: 'leisure' },
  { category: 'finance', query_key: 'amenity', query_val: 'bank|atm' },
  { category: 'accommodation', query_key: 'tourism', query_val: 'hotel|hostel' },
  { category: 'culture', query_key: 'tourism', query_val: 'museum|art_gallery' },
  { category: 'service', query_key: 'amenity', query_val: 'post_office|police' },
  { category: 'nature', query_key: 'leisure', query_val: 'park|garden' }
];

// Flattened Task Generation
// Create a task for every combination of Station + Category
const tasks = [];

nodes.forEach(station => {
  // Optional: Skip Ueno if we want to save time (since it's done)
  // if (station.id === 'tokyo.jr.ueno') return;

  categories.forEach(cat => {
    tasks.push({
      json: {
        stationId: station.id,
        stationName: station.name?.en || station.id,
        lat: station.location?.lat,
        lon: station.location?.lng,
        category: cat.category,
        query_key: cat.query_key,
        query_val: cat.query_val || ''
      }
    });
  });
});

return tasks;

// Radius 300m
const item = items[0].json;
// Safety Check
if (!item.lat || !item.lon) return [{ json: { skipped: true, reason: 'No Location' } }];

const radius = 300;
let selector = `["${item.query_key}"]`;
if (item.query_val) {
  selector = `["${item.query_key}"~"${item.query_val}"]`;
}
// Timeout 25s
const rawQuery = `[out:json][timeout:25];(node${selector}(around:${radius},${item.lat},${item.lon});way${selector}(around:${radius},${item.lat},${item.lon}););out center body;`;
const queryEncoded = encodeURIComponent(rawQuery);
return [{ json: { ...item, query: rawQuery, query_encoded: queryEncoded } }];

// Robust Context Lookup V23
// Same logic as V22

try {
  const context = $('SplitInBatches').first().json;

  if (!context || !context.stationId) {
    return [{ json: { skipped: true, reason: 'Context Missing' } }];
  }

  // Check for previous skipped steps usage
  if (items[0].json.skipped || items[0].json.error) {
      return [{ json: { skipped: true, reason: 'Previous Step Skipped/Error' } }];
  }

  const apiResponse = items[0].json;
  if (items[0].error || !apiResponse.elements) {
     return [{ json: { skipped: true, reason: 'API Error or Empty' } }];
  }

  const elements = apiResponse.elements || [];

  const processed = elements.map(el => {
    const tags = el.tags || {};
    if (!tags.name) return null;
    return {
      osm_id: el.id,
      name: tags.name,
      category: context.category,
      location: { lat: el.lat || el.center?.lat, lon: el.lon || el.center?.lon },
      tags
    };
  }).filter(Boolean);

  return [{
    json: {
      stationId: context.stationId,
      category: context.category,
      count: processed.length,
      items: processed
    }
  }];

} catch (e) {
  return [{ json: { skipped: true, error: e.message } }];
}
