// Define Categories to fetch sequentially
return [
  { json: { category: 'shopping', query_key: 'shop' } },
  { json: { category: 'dining', query_key: 'amenity', query_val: 'restaurant|cafe|fast_food' } },
  { json: { category: 'medical', query_key: 'amenity', query_val: 'pharmacy|clinic|hospital|dentist' } },
  { json: { category: 'leisure', query_key: 'leisure' } },
  { json: { category: 'education', query_key: 'amenity', query_val: 'school|university|kindergarten' } },
  { json: { category: 'finance', query_key: 'amenity', query_val: 'bank|atm' } },
  { json: { category: 'accommodation', query_key: 'tourism', query_val: 'hotel|hostel|guest_house' } },
  { json: { category: 'culture', query_key: 'tourism', query_val: 'museum|art_gallery' } },
  { json: { category: 'service', query_key: 'amenity', query_val: 'post_office|police|townhall' } },
  { json: { category: 'nature', query_key: 'leisure', query_val: 'park|garden' } }
];

// Construct Overpass Query for THIS single category
const category = items[0].json.category;
const key = items[0].json.query_key;
const val = items[0].json.query_val || '';
const lat = 35.7141; // Default Ueno
const lon = 139.7774;
const radius = 1000;

// Construct selector
let selector = `["${key}"]`;
if (val) {
  selector = `["${key}"~"${val}"]`;
}

const query = `
[out:json][timeout:25];
(
  node${selector}(around:${radius},${lat},${lon});
  way${selector}(around:${radius},${lat},${lon});
  relation${selector}(around:${radius},${lat},${lon});
);
out center body;
`;

// Pass both the query AND the context metadata
return [
  {
    json: {
      query,
      category,
      target_lat: lat,
      target_lon: lon
    }
  }
];

// Process Merged Data 
// items[0] contains the merged result of OSM API (Input 1) and Copy of Context (Input 2)

// Depending on n8n version, MergeByIndex merges properties. 
// Checks: 
const osmData = items[0].json.elements ? items[0].json : items[0].json  // Usually index 0 primary
const contextData = items[0].json.category ? items[0].json : (items[1] ? items[1].json : {});

const elements = items[0].json.elements || [];
const category = items[0].json.category || 'unknown'; // Derived from retrieval
const origin = {
  lat: items[0].json.target_lat || 0,
  lon: items[0].json.target_lon || 0
};

const processed = elements.map(el => {
  const tags = el.tags || {};
  if (!tags.name) return null;

  const lat = el.lat || el.center?.lat;
  const lon = el.lon || el.center?.lon;

  return {
    osm_id: el.id,
    name: tags.name,
    name_en: tags["name:en"] || null,
    category: category,
    location: { lat, lon },
    tags: tags
  };
}).filter(i => i !== null);

return [
  {
    json: {
      category,
      count: processed.length,
      items: processed,
      origin
    }
  }
];
