UPDATE nodes
SET riding_knowledge = '{"traps":[{"icon":"🎯","title":"出口分散","description":"丸之內線池袋站有多個出口（b1、b2、c1 等），不同出口通往不同方向。","advice":"b1、b2 出口通往東口方向；c1 出口通往西口方向。"},{"icon":"🔄","title":"與JR站距離","description":"丸之內線池袋站位於 JR 池袋站的西側，步行約 3-5 分鐘。","advice":"站內有地下通道連通，跟隨「JR」指標前進即可。"},{"icon":"🏔️","title":"地下四層","description":"副都心線池袋站位於地下四層，是東京 Metro 較深的月台之一。","advice":"需要搭乘電扶梯或電梯才能抵達地面，請預留充裕時間。"},{"icon":"🚃","title":"直通運行","description":"副都心線與東橫線、橫濱高速鐵道直通運行，可直達橫濱方向。","advice":"確認看板上的直通運行資訊，並非所有列車都直通。"},{"icon":"🎫","title":"與JR站分隔","description":"西武池袋站位於 JR 池袋站東口的東側，步行約 3-5 分鐘。站內有地下通道連通。","advice":"從 JR 東口出站後，跟隨「西武池袋」指標前進即可。"},{"icon":"🏪","title":"西武百货","description":"西武池袋站直結「西武池袋百货」，是東京最大的西武百货之一。","advice":"百货公司內有許多餐廳和美食廣場，適合用餐和購物。"},{"icon":"🏪","title":"東武百货","description":"東武池袋站直結「東武池袋百货」，是東京最大的東武百货之一。","advice":"與西武百货相鄰，但定位略有不同。"},{"icon":"🏬","title":"西武・東武百貨店悖論","description":"西武百货店在车站东侧（靠近西武池袋站），东武百货店在西侧（靠近东武池袋站。","advice":"两家百货店无直接地下通道连接，穿行需出站步行约800m，耗时12-15分钟。"},{"icon":"🚇","title":"副都心線距離","description":"副都心线月台位于JR站体南侧，从JR东口步行约350m，需5-7分钟。","advice":"转乘副都心线建议使用南通路，不要尝试从北口绕行。"},{"icon":"🕳️","title":"地下迷宮","description":"站内存在3层地下空间，B1为商业街（Ikebukuro Underground Shopping Center），B2为通路，B3为部分线路月台。","advice":"以地面标志灯颜色识别方向：红色通往JR，蓝色通往东武/西武，绿色通往地铁。"},{"icon":"🚪","title":"東口與西口差異","description":"东口连接JR线，西口主要连接东武线和东武百货店。","advice":"目的地若在东武百货店，必须从西口出站，从东口出站后步行至西口需10分钟。"}],"hacks":[{"icon":"🏪","title":"丸之內大樓","description":"車站直結的購物中心，有超市和餐廳。"},{"icon":"🏢","title":"東京藝術劇場","description":"從丸之內線出口步行約 5 分鐘可達，是欣賞表演藝術的好去處。"},{"icon":"🚃","title":"直通橫濱","description":"副都心線直通東橫線，可直達橫濱站，是前往橫濱的便捷選擇。"},{"icon":"🎭","title":"文化設施","description":"附近有許多劇院和表演場地，是東京的文化中心之一。"},{"icon":"🛍️","title":"西武百货","description":"站直結的百货公司，是購物和用餐的好去處。地下食品館有各種日本美食。"},{"icon":"🚃","title":"川越方向","description":"西武線可直達川越站，是前往小江戶的便捷選擇。川越以菓子屋橫丁和冰川神社聞名。"},{"icon":"🌸","title":"嚕嚕米樂園","description":"從西武線的「所澤站」可達嚕嚕米樂園，是家庭出遊的好選擇。"},{"icon":"🛍️","title":"東武百货","description":"站直結的百货公司，適合購物和用餐。頂樓有餐廳街，可俯瞰東京市景。"},{"icon":"🌸","title":"日光・鬼怒川","description":"東武線可直達日光站和鬼怒川溫泉，是東京近郊溫泉和賞楓的熱門目的地。"},{"icon":"🎯","title":"西武線快速換乘","description":"使用西武池袋站（与JR池袋站相邻但独立）时，从JR东口出站后右转步行50m即可到达西武入口。","advice":"闸机外换乘需出站后重新进站，若使用Suica需确保余额充足。"},{"icon":"🎯","title":"東武線捷徑","description":"东武池袋站位于JR站西侧，从JR西口出站后左转步行80m可见东武入口标志。","advice":"东武线与JR线闸机不相通，换乘必须出站。"},{"icon":"🎯","title":"地下連通捷徑","description":"Ikebukuro Underground Shopping Center (B1F) 连接东口、西口、南口及周边大楼。","advice":"下雨天可全程使用地下通道前往西武/东武百货店，无需携带雨具。"},{"icon":"🎯","title":"行李寄放策略","description":"JR站东口外及B1F设有大型行李寄存柜（300円/次），尺寸可容纳29寸行李箱。","advice":"旺季建议上午10点前寄存，午后柜位常满。"}],"facilities":[{"type":"toilet","location":"改札內","tags":["Wheelchair","BabyRoom"]},{"type":"elevator","location":"北口、 東口","tags":["Wheelchair"]},{"type":"wifi","location":"全站","tags":[]},{"type":"elevator","location":"中央、北口、東口、西口","tags":["Wheelchair"]},{"type":"elevator","location":"b1、b2 出口","tags":["Wheelchair"]},{"type":"elevator","location":"主要出入口及各月台","tags":["Wheelchair"]},{"type":"toilet","location":"西武百货內","tags":["BabyRoom"]},{"type":"elevator","location":"主要出入口及月台","tags":["Wheelchair"]},{"type":"toilet","location":"東武百货內","tags":["BabyRoom"]}]}'
WHERE id = 'odpt:Station:TokyoMetro.HigashiIkebukuro';


