-- Update for odpt:Station:JR-East.Iidabashi
UPDATE nodes
SET riding_knowledge = '{"traps":[{"icon":"🎢","title":"五線交匯長廊 (Five-Line Corridor)","description":"飯田橋站匯集了東西線、有楽町線、南北線、大江戶線與 JR，轉乘路徑極長。","advice":"️ 注意：東西線與其他線路轉乘需步行 5-10 分鐘，請務必跟隨地面顏色指引指標。"},{"icon":"🔄","title":"與其他線距離","description":"東西線飯田橋站位於較深的位置，與大江戶線、JR 站的轉乘需要步行較長距離。","advice":"跟隨「○色」的地面指標前進，可加快轉乘速度。"},{"icon":"🎯","title":"位置","description":"南北線飯田橋站與東西線站相鄰，但屬於不同月台。","advice":"兩站之間有地下通道連通，步行約 3 分鐘。"},{"icon":"🚃","title":"直通","description":"南北線與三田線直通運行，可直達目黑、白金方向。","advice":"確認看板上的直通運行資訊。"},{"icon":"🌀","title":"地下六層","description":"大江戶線飯田橋站位於地下六層，是東京較深的月台之一。","advice":"需要搭乘電扶梯或電梯才能抵達地面，請預留充裕時間。"},{"icon":"🔄","title":"轉乘距離","description":"大江戶線站與東京Metro站的轉乘需要步行較長距離。","advice":"跟隨「東京Metro」指標前進，會經過多段電扶梯。"},{"icon":"🎯","title":"位置偏遠","description":"JR 飯田橋站位於東京Metro車站的西側，步行約 5-8 分鐘。","advice":"站內有地下通道連通，跟隨「東京Metro」指標前進即可。"},{"icon":"🚃","title":"中央・總武線","description":"中央・總武線（各站停車）的月台位置特殊，與其他線路不在同一處。","advice":"確認看板上的乘車位置，避免走錯月台。"}],"hacks":[{"icon":"⛩️","title":"東京大神宮","description":"從西口出站步行約 5 分鐘，是東京最具代表性的戀愛結緣神社。單身者必訪！"},{"icon":"🚢","title":"Canal Cafe","description":"位於神田川邊的景觀咖啡廳，特別是櫻花季時美不勝收。建議預約靠窗座位。"},{"icon":"🌸","title":"千鳥之淵","description":"從 B1 出口步行約 5 分鐘可達，是東京著名的賞櫻景點。划船體驗非常受歡迎。"},{"icon":"🏛️","title":"日本武道館","description":"步行約 10 分鐘可達，是舉辦演唱會和武術比賽的著名場地。"},{"icon":"🏞️","title":"自然環境","description":"飯田橋站周邊有許多公園和河流，適合散步和運動。"},{"icon":"🍜","title":"美食推薦","description":"周邊有許多餐廳和咖啡廳，適合用餐和休息。"},{"icon":"🏛️","title":"皇居參觀","description":"從飯田橋站可步行至皇居東御苑，是參觀日本皇室住所的好去處。"},{"icon":"🏸","title":"運動設施","description":"周邊有許多運動設施和公園，適合運動愛好者。"},{"icon":"🏢","title":"商業區","description":"飯田橋站周邊是東京的商業區，有許多辦公大樓和餐廳。"},{"icon":"🌉","title":"神田川","description":"沿著神田川散步，可欣賞城市風光和自然景色。"}],"facilities":[{"type":"toilet","location":"改札內","tags":["Wheelchair","BabyRoom"]},{"type":"elevator","location":"A1、A2、A3、A4 出口","tags":["Wheelchair"]},{"type":"wifi","location":"全站","tags":[]},{"type":"elevator","location":"主要出入口","tags":["Wheelchair"]},{"type":"elevator","location":"主要出入口及各月台","tags":["Wheelchair"]}]}'
WHERE id = 'odpt:Station:JR-East.Iidabashi';


