-- Update for odpt:Station:Keikyu.Kitashinagawa
UPDATE nodes
SET riding_knowledge = '{"traps":[{"icon":"🎫","title":"與 JR 分隔","description":"京急品川站位於 JR 品川站的東側，步行約 3-5 分鐘。","advice":"站內有地下通道連通，跟隨「JR」指標前進即可。"},{"icon":"🚃","title":"機場方向","description":"京急線分為「普通」「機場快特」「快特」等不同種類，停靠站不同。","advice":"前往機場請搭乘「機場快特」或「快特」，停靠羽田機場。"},{"icon":"🎯","title":"山手線內回外回","description":"山手線有內回（順時針）和外回（逆時針）兩個方向，月台在中間而非兩側。","advice":"確認看板上的「品川・東京方向」或「新宿・池袋方向」。"},{"icon":"🚃","title":"與其他線關係","description":"山手線品川站與其他路線的轉乘需要步行，但有地下通道連通。","advice":"跟隨「京急」或「東海道線」指標前進。"}],"hacks":[{"icon":"✈️","title":"羽田機場直達","description":"京急線可直達羽田機場，約 20 分鐘可達，是最便捷的機場交通之一。"},{"icon":"🏙️","title":"橫濱方向","description":"京急線可直達橫濱站，是前往橫濱中華街和港未來區的便捷選擇。"},{"icon":"🌸","title":"三浦半島","description":"京急線可直達三浦半島，那裡有美麗的海灘和溫泉。"},{"icon":"🚃","title":"環狀遊東京","description":"山手線可繞東京一圈，是遊覽東京各景點的便捷方式。"},{"icon":"🏢","title":"港南區","description":"品川站周邊是港南區，有許多高科技企業和研究中心。"}],"facilities":[{"type":"toilet","location":"改札內","tags":["Wheelchair","BabyRoom"]},{"type":"elevator","location":"北口、高輪改札","tags":["Wheelchair"]},{"type":"wifi","location":"部分區域","tags":[]},{"type":"wifi","location":"全站","tags":[]},{"type":"elevator","location":"主要出入口","tags":["Wheelchair"]}]}'
WHERE id = 'odpt:Station:Keikyu.Kitashinagawa';


