-- Update for odpt:Station:JR-East.KitaSenju
UPDATE nodes
SET riding_knowledge = '{"traps":[{"icon":"🎯","title":"四線交會","description":"北千住站有 JR、日比谷線、東武線、TX 四條路線交會，轉乘複雜。","advice":"不同路線在不同位置，請跟隨指標前進，避免迷路。"},{"icon":"🚃","title":"常磐線分歧","description":"常磐線有「快速」「區間快速」「各站停車」等不同種類，停靠站不同。","advice":"確認看板上的列車種類和目的地。"},{"icon":"🎫","title":"與東武線分隔","description":"JR 北千住站和東武北千住站是兩個不同的車站，需要步行轉乘。","advice":"從 JR 西口出站，跟隨「東武」指標前進，步行約 5 分鐘。"},{"icon":"🎯","title":"與 JR 站關係","description":"日比谷線北千住站與 JR 站相鄰，但屬於不同車站。","advice":"站內有地下通道連通，跟隨「JR」指標前進即可。"},{"icon":"🚃","title":"與東武線","description":"日比谷線和東武線的轉乘需要出站步行約 5 分鐘。","advice":"使用 Suica/PASMO 可直接刷卡通過連通道。"},{"icon":"🎫","title":"與 JR 分隔","description":"東武北千住站位於 JR 站的西側，步行約 5 分鐘。","advice":"從 JR 西口出站，跟隨「東武」指標前進即可。"},{"icon":"🚃","title":"天空樹線","description":"東武的「東京天空樹線」是從北千住到押上站的支線，與主線不同。","advice":"確認看板上的目的地，避免搭錯車。"},{"icon":"🎯","title":"位置偏遠","description":"TX 北千住站位於其他三個車站的北側，步行需要較長時間。","advice":"TX 站的位置與 JR、日比谷線、東武站有一段距離，請預留充裕時間。"},{"icon":"🎫","title":"票價較高","description":"筑波快線的票價比一般地鐵高，尤其是往東京方向的區間。","advice":"若預算有限，建議使用其他路線前往東京。"},{"icon":"🕐","title":"班距較長","description":"TX 的班距比東京 Metro 長，尖峰時段約 3-5 分鐘，離峰時段可能長達 10 分鐘以上。","advice":"建議查詢 TX 官方 APP 確認時刻表。"}],"hacks":[{"icon":"🏪","title":"業務超市","description":"北千住站周邊有多家業務超市，可以購買便宜的日本零食和飲料。"},{"icon":"🌸","title":"荒川沿岸","description":"荒川沿岸是散步和騎腳踏車的好去處，春天有美麗的櫻花。"},{"icon":"🏛️","title":"隅田川","description":"步行可達隅田川，是欣賞河景和散步的好去處。"},{"icon":"🚃","title":"直達東京","description":"日比谷線可直達銀座、日比谷、六本木、赤坂方向的東京市中心。"},{"icon":"🏙️","title":"便利交通","description":"從北千住搭乘日比谷線可快速抵達東京各大景點。"},{"icon":"🗼","title":"晴空塔直達","description":"東武天空樹線可直達東京晴空塔站，是前往晴空塔的便捷選擇。"},{"icon":"🚃","title":"日光・鬼怒川","description":"東武線可直達日光站和鬼怒川溫泉，是東京近郊溫泉和賞楓的熱門目的地。"},{"icon":"🛍️","title":"購物推薦","description":"東武車站內有許多商店和餐廳，適合用餐和購物。"},{"icon":"🚄","title":"筑波直達","description":"TX 是前往筑波科學城的便捷方式，約 45 分鐘可達。"},{"icon":"✈️","title":"機場交通","description":"TX 可從秋葉原直達成田機場，是前往機場的替代選擇。"}],"facilities":[{"type":"toilet","location":"改札內","tags":["Wheelchair"]},{"type":"elevator","location":"出口","tags":["Wheelchair"]},{"type":"wifi","location":"部分區域","tags":[]},{"type":"toilet","location":"西口改札內","tags":["Wheelchair","BabyRoom"]},{"type":"toilet","location":"東口改札內","tags":["Wheelchair"]},{"type":"elevator","location":"西口、東口","tags":["Wheelchair"]},{"type":"elevator","location":"主要出入口","tags":["Wheelchair"]},{"type":"wifi","location":"全站","tags":[]}]}'
WHERE id = 'odpt:Station:JR-East.KitaSenju';


